import React, { useState } from "react";
import "./toggle.css"
const Toggle = () => {
  //logic
  const [isActive, setIsActive] = useState(false); //[flase, function]
  const handleLick = ()=>{
    setIsActive(true)
  }
  console.log(">>>>>>check toggle: ", isActive);
  return (
    <div className="toggle" onClick={()=>handleLick()}>
      <div className="circle" className={isActive ? "circle isActive" : ""}></div>
    </div>
  );
};

export default Toggle;
